mapper.select:
SELECT p.*, c.category_name, b.brand_name FROM t_product p LEFT JOIN t_category c ON p.category_id = c.id LEFT JOIN t_brand b ON p.brand_id = b.id WHERE p.deleted = 0
AND p.product_name LIKE CONCAT('%', #{productName}, '%')
AND p.product_code LIKE CONCAT('%', #{productCode}, '%')
AND p.category_id = #{categoryId}
AND p.brand_id = #{brandId}
AND p.status = #{status}
ORDER BY p.sort_order ASC, p.id DESC
